import React, { Component } from 'react'
import NewsItem from './NewsItem'
export class News extends Component {



  constructor() {
    super();
    console.log("I am from constructor");
    this.state = {
      articles: [],
      loading: false
    }
  }

  async componentDidMount() {
    console.log("I am from component did mount");
    let url = "https://newsapi.org/v2/everything?q=technology&apiKey=171e9d195b274cc59c9b8b94060454e3";
    let data = await fetch(url);
    let parsedData = await data.json()
    console.log(parsedData);
    this.setState({articles:parsedData.articles});

    }

  render()
  {
      return(
      <div className = 'container my-3 border border border-dark  float-right' style={{borderRadius:'10px'}} >
        <h2>Top headlines of today</h2>

        <div className="row my-3  ">
          {this.state.articles.map((element) => {
            return <div className='col-md-4 ' key={element.url}>
              <NewsItem title={element.title} description={element.description} imageUrl={element.urlToImage}  newsUrl={element.url}/>
            </div>
          })}

        </div>
      </div>
    );
  }
}

export default News;
